def prime_test_exhaustive(x: int) -&gt; bool:
  """Determine if positive input is prime, returning true for prime and false for not prime."""
  if x &lt;= 1:
    return False
  if x == 2:
    return True
  for guess in range(2, x, 1):
    if x % guess == 0:
      return False
  return True






